import './App.css';
import gptLogo from './asset/chatgpt.svg';
import addBtn from './asset/add-30.png';
import msgIcon from './asset/message.svg';
import home from './asset/home.svg';
import saved from './asset/bookmark.svg';
import rocket from './asset/rocket.svg';
import sendBtn from './asset/send.svg';
import userIcon from './asset/user-icon.png';
import gptImgLogo from './asset/chatgptLogo.svg'
import {sendMsgToOpenAI} from "./openai";
import {useState} from "react";

function App() {
    const [input, setInput] = useState();

    const handleSend = () => {
        const res = sendMsgToOpenAI()
    }

  return (
    <div className="App">
      <div className="sideBar">
        <div className="upperSide">
          <div className="upperSideTop"><img src={gptLogo} alt="Logo" className="logo"/><span className="brand">ChatGPT</span></div>
          <button className="midBtn"><img src={addBtn} alt="New Chat" className="addBtn"/>New Chat</button>
          <div className="upperSideBottom">
            <button className="query"><img src={msgIcon} alt="Query"/>What is Programming?</button>
            <button className="query"><img src={msgIcon} alt="Query"/>How to use an API?</button>
          </div>
        </div>
          <div className="lowerSide">
              <div className="listItems"><img src={home} alt="Home" className="listItemsImg"/>Home</div>
              <div className="listItems"><img src={saved} alt="Saved" className="listItemsImg"/>Saved</div>
              <div className="listItems"><img src={rocket} alt="Rocket" className="listItemsImg"/>Upgrade to Pro</div>
          </div>
      </div>
        <div className="main">
            <div className="chats">
                <div className="chat">
                    <img className='chatImg' src={userIcon} alt=""/><p className="txt">Test</p>
                </div>
                <div className="chat bot">
                    <img className='chatImg' src={gptImgLogo} alt=""/><p className="txt">Test</p>
                </div>
            </div>
            <div className="chatFooter">
                <div className="inp">
                    <input type="text" placeholder='Send a message...' value={input} onChange={(e)=>{setInput((e.target.value))}}/><button className="send" onClick={handleSend }><img src={sendBtn} alt="Send"/></button>
                </div>
                <p>ChatGPT may produce inaccurate information about people or facts. ChatGPT December 16 Version</p>
            </div>
        </div>
    </div>
  );
}

export default App;
